import { List, ListItem, ListItemAvatar, Avatar, ListItemText, Typography } from '@mui/material';
import { format } from 'date-fns';

export default function ConversationList({ conversations, onSelectChat, selectedId }) {
  return (
    <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper', borderRight: '1px solid #e0e0e0' }}>
      {conversations.map((conversation) => (
        <ListItem 
          key={conversation._id} 
          button 
          selected={selectedId === conversation._id}
          onClick={() => onSelectChat(conversation)}
        >
          <ListItemAvatar>
            <Avatar>{conversation.name.charAt(0)}</Avatar>
          </ListItemAvatar>
          <ListItemText
            primary={conversation.name}
            secondary={
              <Typography
                component="span"
                variant="body2"
                color="text.secondary"
                noWrap
              >
                {conversation.lastMessage}
              </Typography>
            }
          />
          <Typography variant="caption" color="text.secondary">
            {format(new Date(conversation.lastTime), 'h:mm a')}
          </Typography>
        </ListItem>
      ))}
    </List>
  );
}
