import { Box, Typography } from '@mui/material';
import { format } from 'date-fns';
import { Check, DoneAll } from '@mui/icons-material';

const MessageText = ({ text }) => {
  return (
    <div style={{
      fontSize: '14.2px',
      lineHeight: '19px',
      fontFamily: 'Segoe UI, system-ui, -apple-system, sans-serif',
      padding: '2px 0',
      whiteSpace: 'pre-wrap',
      wordWrap: 'break-word',
      overflowWrap: 'break-word',
      wordBreak: 'break-word',
      '& a': {
        color: '#128C7E',
        textDecoration: 'none',
        '&:hover': {
          textDecoration: 'underline'
        }
      }
    }}>
      {text.split('\n').map((line, i, arr) => (
        <div key={i} style={{ 
          minHeight: '19px',
          position: 'relative',
          padding: '0 0 1px 0',
          wordBreak: 'break-word'
        }}>
          {line || ' '}
        </div>
      ))}
    </div>
  );
};

export default function MessageBubble({ message, isOwnMessage }) {
  return (
    <Box
      sx={{
        maxWidth: '65%',
        margin: '0.2em 0 0.5em',
        marginLeft: isOwnMessage ? 'auto' : '0.5em',
        marginRight: isOwnMessage ? '0.5em' : 'auto',
        position: 'relative',
        '&:first-of-type': {
          marginTop: '0.5em'
        }
      }}
    >
      <Box
        sx={{
          backgroundColor: isOwnMessage ? '#DCF8C6' : 'white',
          borderRadius: '7.5px',
          padding: '6px 7px 8px 9px',
          boxShadow: '0 1px 0.5px rgba(0,0,0,0.13)',
          position: 'relative',
          width: 'fit-content',
          maxWidth: '100%',
          marginLeft: isOwnMessage ? 'auto' : '0',
          '&:hover': {
            boxShadow: '0 1px 0.5px rgba(0,0,0,0.2)'
          },
          '&:before': {
            content: '""',
            position: 'absolute',
            width: '8px',
            height: '13px',
            [isOwnMessage ? 'right' : 'left']: '-6px',
            top: '0',
            backgroundImage: isOwnMessage 
              ? 'url("data:image/svg+xml;charset=utf-8,<svg xmlns=\'http://www.w3.org/2000/svg\' viewBox=\'0 0 8 13\' width=\'8\' height=\'13\'><path opacity=\'.13\' d=\'M5.188 1H0v11.193l6.467-8.625C7.526 2.156 6.958 1 5.188 1z\' fill=\'%23000000\'/><path fill=\'%23DCF8C6\' d=\'M5.188 0H0v11.193l6.467-8.625C7.526 1.156 6.958 0 5.188 0z\'/></svg>")' 
              : 'url("data:image/svg+xml;charset=utf-8,<svg xmlns=\'http://www.w3.org/2000/svg\' viewBox=\'0 0 8 13\' width=\'8\' height=\'13\'><path opacity=\'.13\' fill=\'%23000\' d=\'M1.533 3.568L8 12.193V1H2.812C1.042 1 .474 2.156 1.535 3.568z\'/><path opacity=\'1\' fill=\'%23fff\' d=\'M1.533 2.568L8 11.193V0H2.812C1.042 0 .474 1.156 1.535 2.568z\'/></svg>")',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: isOwnMessage ? '0 0' : '100% 0',
            [isOwnMessage ? 'transform' : '']: 'scaleX(-1)'
          }
        }}
      >
        <MessageText text={message.text} />
        <Box sx={{ 
          display: 'flex', 
          justifyContent: 'flex-end',
          alignItems: 'center',
          marginTop: '2px',
          height: '15px',
          position: 'relative',
          float: 'right',
          right: '-4px',
          bottom: '-1px',
          marginLeft: '4px'
        }}>
          <Typography 
            component="span"
            sx={{ 
              color: 'rgba(0,0,0,0.45)',
              fontSize: '11px',
              lineHeight: '15px',
              verticalAlign: 'top',
              userSelect: 'none',
              marginRight: '3px'
            }}
          >
            {format(new Date(message.timestamp), 'h:mm a')}
          </Typography>
          {isOwnMessage && (
            <span style={{ marginLeft: '3px', lineHeight: 0 }}>
              {message.status === 'sent' ? (
                <Check fontSize="inherit" sx={{ fontSize: '16px', color: 'rgba(0,0,0,0.45)' }} />
              ) : (
                <DoneAll fontSize="inherit" sx={{ fontSize: '16px', color: '#53bdeb' }} />
              )}
            </span>
          )}
        </Box>
      </Box>
    </Box>
  );
}
